var browser = {
    canUse: function (feature) {
      var testElement = document.createElement("div");
      var style = testElement.style;
      var capitalizedFeature = feature.charAt(0).toUpperCase() + feature.slice(1);
  
      return (
        feature in style ||
        "Moz" + capitalizedFeature in style ||
        "Webkit" + capitalizedFeature in style ||
        "O" + capitalizedFeature in style ||
        "ms" + capitalizedFeature in style
      );
    },
  };
  
  // Example usage:
  if (browser.canUse("flexbox")) {
    // Your code for browsers that support the 'flexbox' feature
  } else {
    // Your fallback code for browsers that do not support 'flexbox'
  }
  